document.addEventListener("DOMContentLoaded",()=>{let e=0;const t=document.querySelectorAll(".carousel img"),n=t.length;function c(e){t.forEach(e=>e.classList.remove("active")),t[e].classList.add("active")}setInterval(()=>{e=(e+1)%n,c(e)},3e3),document.querySelector(".next").addEventListener("click",()=>{e=(e+1)%n,c(e)}),document.querySelector(".prev").addEventListener("click",()=>{e=(e-1+n)%n,c(e)});const o=document.querySelector(".menu-toggle"),a=document.querySelector("nav ul");o.addEventListener("click",()=>{o.classList.toggle("active"),a.classList.toggle("active")});const s=document.getElementById("cadastro-form"),r=document.getElementById("form-message");s.addEventListener("submit",e=>{e.preventDefault(),s.checkValidity()?(r.textContent="Cadastro realizado com sucesso!",r.style.color="green",s.reset()):(r.textContent="Por favor, preencha todos os campos corretamente.",r.style.color="red")})});
